"use client";

import { ConfirmTooltip } from "@/components/ui/animated-tooltip";
import { Button } from "@/components/ui/button";
import { Trash2 } from "lucide-react";
import { useState } from "react";

export function AnimatedTooltipConfirmDemo() {
  const [deletedItems, setDeletedItems] = useState<string[]>([]);

  const handleDelete = (item: string) => {
    setDeletedItems(prev => [...prev, item]);
  };

  return (
    <div className="flex flex-col items-center gap-4">
      <div className="flex gap-2">
        <ConfirmTooltip
          message="Are you sure you want to delete this item? This action cannot be undone."
          onConfirm={() => handleDelete("Item 1")}
          confirmText="Delete"
          cancelText="Cancel"
        >
          <Button variant="destructive" size="sm">
            <Trash2 className="mr-2 h-4 w-4" />
            Delete Item 1
          </Button>
        </ConfirmTooltip>

        <ConfirmTooltip
          message="This will permanently remove the file from your system."
          onConfirm={() => handleDelete("File")}
          confirmText="Yes, delete"
          cancelText="Keep it"
        >
          <Button variant="destructive" size="sm">
            <Trash2 className="mr-2 h-4 w-4" />
            Delete File
          </Button>
        </ConfirmTooltip>
      </div>

      {deletedItems.length > 0 && (
        <div className="text-sm text-muted-foreground">
          Deleted: {deletedItems.join(", ")}
        </div>
      )}
    </div>
  );
}
