"use client";

import { TooltipGroup } from "@/components/ui/animated-tooltip";
import { AlignCenter, AlignLeft, AlignRight, Bold, Italic, Underline } from "lucide-react";

export function AnimatedTooltipGroupDemo() {
  const formattingItems = [
    { icon: <Bold className="h-4 w-4" />, label: "Bold", shortcut: "⌘B" },
    { icon: <Italic className="h-4 w-4" />, label: "Italic", shortcut: "⌘I" },
    { icon: <Underline className="h-4 w-4" />, label: "Underline", shortcut: "⌘U" },
  ];

  const alignmentItems = [
    { icon: <AlignLeft className="h-4 w-4" />, label: "Align Left", shortcut: "⌘L" },
    { icon: <AlignCenter className="h-4 w-4" />, label: "Align Center", shortcut: "⌘E" },
    { icon: <AlignRight className="h-4 w-4" />, label: "Align Right", shortcut: "⌘R" },
  ];

  return (
    <div className="flex flex-col items-center gap-4">
      <TooltipGroup items={formattingItems} />
      <TooltipGroup items={alignmentItems} />
    </div>
  );
}
