"use client";

import { useState } from "react";
import { CursorFollower } from "@/registry/default/ui/cursor-follower";

export default function CursorFollowerDemo() {
  const [isActive, setIsActive] = useState(false);

  return (
    <div className="space-y-4">
      <div className="text-center">
        <h3 className="text-lg font-semibold mb-2">Basic Cursor Follower</h3>
        <p className="text-sm text-muted-foreground mb-4">
          Click the area below to activate the cursor follower.
        </p>
      </div>

      <div
        className="h-96 bg-muted/30 rounded-lg flex items-center justify-center cursor-pointer"
        onClick={() => setIsActive(!isActive)}
      >
        <div className="text-center">
          <p className="text-muted-foreground mb-2">
            {isActive ? "‚óè Active! Move your cursor" : "Click to activate cursor follower"}
          </p>
          <button className="px-4 py-2 bg-primary text-primary-foreground rounded-lg hover:bg-primary/90 transition-colors">
            {isActive ? "Deactivate" : "Activate"} Cursor Follower
          </button>
        </div>
      </div>

      {isActive && <CursorFollower size={24} color="hsl(var(--primary))" smoothness={0.1} />}
    </div>
  );
}
