"use client";

import { useState } from "react";
import { CursorSpotlight } from "@/registry/default/ui/cursor-follower";

export default function CursorSpotlightDemo() {
  const [isActive, setIsActive] = useState(false);

  return (
    <div className="space-y-4">
      <div className="text-center">
        <h3 className="text-lg font-semibold mb-2">Cursor Spotlight</h3>
        <p className="text-sm text-muted-foreground mb-4">
          Click to activate the spotlight effect that follows your cursor.
        </p>
      </div>

      <div
        className="h-96 bg-gradient-to-br from-background to-muted rounded-lg flex items-center justify-center cursor-pointer relative overflow-hidden"
        onClick={() => setIsActive(!isActive)}
      >
        <div className="text-center z-10">
          <p className="text-muted-foreground mb-2">
            {isActive ? "ðŸ”¦ Active! Move your cursor" : "Click to activate spotlight"}
          </p>
          <button className="px-4 py-2 bg-primary text-primary-foreground rounded-lg hover:bg-primary/90 transition-colors">
            {isActive ? "Deactivate" : "Activate"} Spotlight
          </button>
        </div>
      </div>

      {isActive && <CursorSpotlight size={400} opacity={0.2} blur={100} />}
    </div>
  );
}
