"use client";

import { useState } from "react";
import { CursorTrail } from "@/registry/default/ui/cursor-follower";

export default function CursorTrailDemo() {
  const [isActive, setIsActive] = useState(false);

  return (
    <div className="space-y-4">
      <div className="text-center">
        <h3 className="text-lg font-semibold mb-2">Cursor Trail</h3>
        <p className="text-sm text-muted-foreground mb-4">
          Click to activate and move your mouse quickly to see the trailing effect.
        </p>
      </div>

      <div
        className="h-96 bg-muted/30 rounded-lg flex items-center justify-center cursor-pointer"
        onClick={() => setIsActive(!isActive)}
      >
        <div className="text-center">
          <p className="text-muted-foreground mb-2">
            {isActive ? "●●● Active! Move quickly" : "Click to activate cursor trail"}
          </p>
          <button className="px-4 py-2 bg-primary text-primary-foreground rounded-lg hover:bg-primary/90 transition-colors">
            {isActive ? "Deactivate" : "Activate"} Cursor Trail
          </button>
        </div>
      </div>

      {isActive && <CursorTrail trailLength={12} size={16} color="hsl(var(--primary))" />}
    </div>
  );
}
